package changhwan.experiment.sopthomework

import android.graphics.Color
import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.lifecycle.MutableLiveData
import androidx.recyclerview.widget.ItemTouchHelper
import androidx.recyclerview.widget.RecyclerView
import changhwan.experiment.sopthomework.databinding.FragmentFollowerBinding


class FollowerFragment : Fragment(), ItemDragListener {

    private var _binding: FragmentFollowerBinding? = null
    private val binding get() = _binding!!
    private lateinit var followerAdapter: FollowerAdapter
    private lateinit var itemTouchHelper : ItemTouchHelper

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        _binding = FragmentFollowerBinding.inflate(layoutInflater, container, false)
        return binding.root

    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        siteFollowerRecycler()

        binding.followerRecycle.addItemDecoration(CustomMarginDecoration(24))
        binding.followerRecycle.addItemDecoration(CustomDividerDecoration(1f,10f, resources.getColor(R.color.divider),40))

        itemTouchHelper = ItemTouchHelper(ItemTouchHelperCallback(followerAdapter))
        itemTouchHelper.attachToRecyclerView(binding.followerRecycle)
    }

    fun siteFollowerRecycler(){
        followerAdapter = FollowerAdapter(this)

        binding.followerRecycle.adapter = followerAdapter

        followerAdapter.followerData.addAll(
            listOf(
                FollowerData(
                    followerName = MutableLiveData<String>().apply { value = "문다빈" },
                    followerIntro = MutableLiveData<String>().apply { value = "안드로이드 파트장sdfgsdfgsdgsdgdsgdsgdsgdsgdfsgdsgsdfg"},
                    followerImg = MutableLiveData<String>().apply{ value = "data:image/jpeg;base64,"}),
                FollowerData(
                    followerName = MutableLiveData<String>().apply { value = "장혜령" },
                    followerIntro = MutableLiveData<String>().apply { value = "ios 파트장"},
                    followerImg = MutableLiveData<String>().apply{ value = "data:image/jpeg;base64,"}),
                FollowerData(
                    followerName = MutableLiveData<String>().apply { value = "김우영" },
                    followerIntro = MutableLiveData<String>().apply { value = "서버 파트장"},
                    followerImg = MutableLiveData<String>().apply{ value = "data:image/jpeg;base64,"}),
                FollowerData(
                    followerName = MutableLiveData<String>().apply { value = "이호재" },
                    followerIntro = MutableLiveData<String>().apply { value = "문다빈 소환수"},
                    followerImg = MutableLiveData<String>().apply{ value = "data:image/jpeg;base64,"}),
                FollowerData(
                    followerName = MutableLiveData<String>().apply { value = "이강민" },
                    followerIntro = MutableLiveData<String>().apply { value = "아이폰 13 보유자"},
                    followerImg = MutableLiveData<String>().apply{ value = "data:image/jpeg;base64,"}),
            )
        )

        //diffUtill부분 원래는 followerAdapter.notifyDataSetChanged()였음
        followerAdapter.setContact(followerAdapter.followerData)
        //여기까지
    }

    override fun onStartDrag(viewHolder: RecyclerView.ViewHolder) {

    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
}